import React, { useState } from 'react';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import axios from 'axios';
import './App.css';

function App() {
  const [downloadLink, setDownloadLink] = useState('');

  // Function to trigger the file processing
  const processFile = async (file) => {
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/process_file', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const jsonResponse = await response.json();
        const modifiedFileName = jsonResponse.modified_file_name;
        setDownloadLink(modifiedFileName);
      } else {
        console.error('File processing failed');
      }
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  // Function to trigger the file download
  const downloadFile = async (filename) => {
    try {
      const response = await fetch(`/api/download/${filename}`);

      if (response.ok) {
        const fileUrl = await response.text();
        setDownloadLink(fileUrl);
      } else {
        console.error('File download failed');
      }
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <Typography variant="h4">Katz Electronic Conversion App</Typography>
        <Typography style={{ paddingTop: '10px', paddingBottom: '10px' }} variant="body1">
            Upload the KatzLaw Aderant export file (.TAB file)
        </Typography>
        <input
        type="file"
        id="file-input"
        style={{ display: 'none' }}
        onChange={handleFileSelect}
      />
      <label htmlFor="file-input">
      <Button style={{ marginTop: '20px', marginBottom: '10px' }} variant="contained" size="large" component="span">
        Upload File
      </Button>
      </label>
      <div>
      {/* Upload file input */}
      <input type="file" accept=".tab" onChange={(e) => processFile(e.target.files[0])} />

      {/* Trigger the download */}
      {downloadLink && (
        <button onClick={() => downloadFile(downloadLink)}>
          Download Processed File
        </button>
      )}
    </div>
      </header>
    </div>
  );
}

export default App;
