import logo from './logo.svg';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import axios from 'axios';
import './App.css';

function App() {
  const [responseMessage, setResponseMessage] = useState('');

  const handleFileSelect = async (event) => {
    const selectedFile = event.target.files[0];
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('/api/process_file', formData);
      setResponseMessage(response.data.message);
    } catch (error) {
      console.error('Error processing file:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <Typography variant="h4">Katz Electronic Conversion App</Typography>
        <Typography sx={{pt:'10px', pb:'10px'}}variant="body1">Upload the KatzLaw Aderant export file (.TAB file)</Typography>
        <input
        type="file"
        id="file-input"
        style={{ display: 'none' }}
        onChange={handleFileSelect}
      />
      <label htmlFor="file-input">
        <Button sx={{mt:'20px', mb:'10px'}} variant="contained" size="large" component="span">
          Upload File
        </Button>
      </label>
      </header>
    </div>
  );
}

export default App;
